import './Sidebar.css';
import { IonButton, IonIcon, IonContent } from '@ionic/react';
import { closeCircleOutline, cameraOutline, cameraReverseOutline } from "ionicons/icons";
import { startCamera, stopCamera, captureImage, flipCamera } from './Camera';

function Sidebar() {
    return(
        <IonContent>
            <div id="popup" className="sidebar">
                <div id = "controls_row" className= "vertical_div">
                    <a href="javascript:void(0)" className="closebtn" onClick={() => {
                        document.getElementById("popup").style.height = "0";
                    }}>Ã—</a>

                </div>
                <div id = "location_info_row" className = "vertical_div">

                </div>
                <div id = "legacy_photo_row" className = "vertical_div">

                </div>
                <div id = "user_photo_row" className = "vertical_div">

                </div>
                <div id="cameraPreview" className="cameraPreview" style={{visibility: 'hidden'}}>
                    <img id="historicalImage" alt=""></img>
                    <IonButton id="close" className="button" onClick={() => {stopCamera()}}>
                        <IonIcon icon={closeCircleOutline} slot="icon-only"></IonIcon>
                    </IonButton>
                    <IonButton id="capture" className="button" onClick={() => {captureImage()}}>
                        <IonIcon icon={cameraOutline} slot="icon-only"></IonIcon>
                    </IonButton>
                    <IonButton id="flip" className="button" onClick={() => {flipCamera()}}>
                        <IonIcon icon={cameraReverseOutline} slot="icon-only"></IonIcon>
                    </IonButton>
                </div>

                <button id="cameraButton" className="cameraButton" onClick={() => {startCamera()}}>Camera</button>
                <img id="capturedImage" alt="" style={{visibility: 'hidden'}}></img>
            </div>
        </IonContent>
    );
}

function getHistoricalImage(image) {
    const historicalImage = document.getElementById("historicalImage");
    historicalImage.src = image;
}

export { Sidebar, getHistoricalImage };
