import './Camera.css';

function Camera() {
    return(
        <body>
            <div class='camera'>
                <label>
                    <input  style={{display: 'none'}}
                            type="file" 
                            accept="image/*"
                            capture="camera"
                    />
                    Camera
                </label>
            </div>
        </body>
    );
}

export default Camera;
