import './App.css';
import MapContainer from "./MapContainer.js";
import { Sidebar } from './Sidebar.js';
import { IonButton, IonIcon, IonImg } from '@ionic/react';
import { closeCircleOutline, cameraOutline, cameraReverseOutline } from "ionicons/icons";
import { startCamera, stopCamera, captureImage, flipCamera } from './Camera';


function App() {
  return (
    <div className="App">


      <div id="cameraPreview" className="cameraPreview" style={{display: 'none'}}>
        <IonImg id="historicalImage" className="image-overlay" alt=""></IonImg>
        <IonButton id="close" className="button" onClick={() => {stopCamera()}}>
            <IonIcon icon={closeCircleOutline} slot="icon-only"></IonIcon>
        </IonButton>
        <IonButton id="capture" className="button" onClick={() => {captureImage()}}>
            <IonIcon icon={cameraOutline} slot="icon-only"></IonIcon>
        </IonButton>
        <IonButton id="flip" className="button" onClick={() => {flipCamera()}}>
            <IonIcon icon={cameraReverseOutline} slot="icon-only"></IonIcon>
        </IonButton>
      </div>
      <div id="app">
        <MapContainer></MapContainer>
        <Sidebar></Sidebar>
      </div>
    </div>
  );
}

export default App;

// add ion-buttons to the app itself rather than sidebar
// keep open camera button in sidebar
// change logic to catch the id/classNames for new camera preview
