import './Sidebar.css';
import Camera from './CameraScreen';

function Sidebar() {
    return(
        <div>
            <div id="popup" className="sidebar">
                <div id = "controls_row" className = "vertical_div">
                        <a href="javascript:void(0)" className="closebtn" onClick={() => {
                            document.getElementById("popup").style.height = "0";
                        }}>Ã—</a>
                        <button id="camera" className="camera" onClick={() => {Camera()}}>Camera</button>
                </div>
                <div id = "location_info_row" className = "vertical_div">

                </div>
                <div id = "legacy_photo_row" className = "vertical_div">

                </div>
                <div id = "user_photo_row" className = "vertical_div">

                </div>

                <div id="display-cover" className="display-cover">
                    <video autoPlay></video>
                    <canvas className="d-none"></canvas>

                    <div className="video-options">
                        <select name="" id="" className="custom-select">
                            <option value="">Select camera</option>
                        </select>
                    </div>

                    <img className="screenshot-image d-none" alt="" />

                    <div className="controls">
                        <button className="btn btn-danger play" title="Play"><i data-feather="play-circle"></i></button>
                        <button className="btn btn-info pause d-none" title="Pause"><i data-feather="pause"></i></button>
                        <button className="btn btn-outline-success screenshot d-none" title="ScreenShot"><i data-feather="image"></i></button>
                    </div>
                    <a href="javascript:void(0)" id="close-button" className="close-button">Ã—</a>
                </div>
            </div>
        </div>
    );
}

function handleClick(){
    const screen = document.getElementById("display-cover");
    const closeButton = document.getElementById("close-button");

    console.log(screen);

