import './Camera.css';
import { CameraPreview } from '@capacitor-community/camera-preview';
import { IonButton, IonIcon } from '@ionic/react';
import { closeCircleOutline, cameraOutline, cameraReverseOutline } from "ionicons/icons";

class Camera {
    constructor() {
        this.isActive = false;
    }

    startCamera() {
        const cameraButton = document.getElementById("cameraButton");
        cameraButton.style.display = "none";

        /*
        const isActive = document.getElementById("isActive");
        isActive.style.display = "block";
        */

        const capturedImage = document.getElementById("capturedImage");
        capturedImage.style.display = "none";


        const cameraPreviewOptions = {
            position: 'rear',
            parent: 'cameraPreview',
            class: 'cameraPreview',
        };
        CameraPreview.start(cameraPreviewOptions);
    }

    async stopCamera() {
        await CameraPreview.stop();

        /*
        const isActive = document.getElementById("isActive");
        isActive.style.display = "none";
        */

        const cameraButton = document.getElementById("cameraButton");
        cameraButton.style.display = "block";
    }

    async captureImage() {
        const cameraPreviewOptions = {
            quality: 90,
        };

        const result = await CameraPreview.capture(cameraPreviewOptions);

        const capturedImage = document.getElementById("capturedImage");
        capturedImage.src = `data:image/jpeg;base64,${result.value}`;
        capturedImage.style.display = "block";

        stopCamera();
    }

    flipCamera() {
        CameraPreview.flip();
    }

    render() {
        return this.isActive && (
            <div id="isActive">
                <img id="historicalImage" alt=""></img>
                <IonButton id="close" className="button" onClick={() => {stopCamera()}}>
                    <IonIcon icon={closeCircleOutline} slot="icon-only"></IonIcon>
                </IonButton>
                <IonButton id="capture" className="button" onClick={() => {captureImage()}}>
                    <IonIcon icon={cameraOutline} slot="icon-only"></IonIcon>
                </IonButton>
                <IonButton id="flip" className="button" onClick={() => {flipCamera()}}>
                    <IonIcon icon={cameraReverseOutline} slot="icon-only"></IonIcon>
                </IonButton>
            </div>
        );
    }
}

export const { startCamera, stopCamera, captureImage, flipCamera } = new Camera();
