import Camera from './Camera';

function Sidebar() {
    return(
        <div id="popup" className="sidebar">
            <div id = "controls_row" className= "vertical_div">
                <a href="javascript:void(0)" className="closebtn" onClick={() => {
                    document.getElementById("popup").style.height = "0";
                }}>Ã—</a>
                <button id="cameraButton" className="cameraButton" onClick={() => {Camera.startCamera()}}>Camera</button>
            </div>
            <div id = "location_info_row" className = "vertical_div">

            </div>
            <div id = "legacy_photo_row" className = "vertical_div">

            </div>
            <div id = "user_photo_row" className = "vertical_div">

            </div>
            <div id="cameraPreview" className="cameraPreview">
                <button id="close" className="stopCamera" onClick={() => {stopCamera()}}></button>
                <button id="capture" className="captureImage" onClick={() => {captureImage()}}></button>
                <button id="flip" className="flipCamera" onClick={() => {flipCamera()}}></button>
            </div>
        </div>
    );
}

export default Sidebar;
