function findDevice() {
    const constraints = {
        video: true,
    };

    const video = document.querySelector("video");

    navigator.mediaDevices.getUserMedia(constraints).then((stream) => {
        video.srcObject = stream;
    });
}


function Camera() {
    return(
        <div class="camera">
            <video autoplay></video>

            <script src="findDevice()"></script>
        </div>
    );
}

export default Camera;
