import './Sidebar.css';
import { IonButton, IonIcon, IonContent } from '@ionic/react';
import { startCamera, stopCamera, captureImage, flipCamera } from './Camera';

function Sidebar() {
    return(
        <div id="popup" className="sidebar">
            <div id = "controls_row" className= "vertical_div">
                <a href="javascript:void(0)" className="closebtn" onClick={() => {
                    document.getElementById("popup").style.height = "0";
                }}>Ã—</a>
                <IonButton id="cameraButton" className="cameraButton" onClick={() => {startCamera()}}>Camera</IonButton>
            </div>
            <div id = "location_info_row" className = "vertical_div">

            </div>
            <div id = "legacy_photo_row" className = "vertical_div">

            </div>
            <div id = "user_photo_row" className = "vertical_div">

            </div>
            <IonContent id="cameraPreview" className="cameraPreview">
                <img id="historicalOverlay" className="historicalOverlay" src='https://cdn1.vectorstock.com/i/1000x1000/68/95/camera-icon-vector-21756895.jpg'></img>
                <IonButton id="close" className="stopCamera" onClick={() => {stopCamera()}}>
                    <IonIcon name="close-circle" slot="icon-only"></IonIcon>
                </IonButton>
                <IonButton id="capture" className="captureImage" onClick={() => {captureImage()}}>
                    <IonIcon name="camera" slot="icon-only"></IonIcon>
                </IonButton>
                <IonButton id="flip" className="flipCamera" onClick={() => {flipCamera()}}>
                    <IonIcon name="camera-reverse" slot="icon-only"></IonIcon>
                </IonButton>
            </IonContent>
        </div>
    );
}

export default Sidebar;
