import './Camera.css';

/*
function Camera() {
    return(
        <body>
            <div class='camera'>
                <label>
                    <input  style={{display: 'none'}}
                            type="file" 
                            accept="image/*"
                            capture="camera"
                    />
                    Camera
                </label>
            </div>
        </body>
    );
}
*/

function Camera() {
    const constraints = window.constraints = {
        audio: false,
        video: true
    };

    const video = document.querySelector("video");

    navigator.mediaDevices.getUserMedia(constraints).then((stream) => {
        video.srcObject = stream;
    });
}


