import './Camera.css';

function Camera() {
    return(
        <body>
            <div class='camera'>
                <label>
                    <input  style={{display: 'none'}}
                            type="file" 
                            accept="image/*" 
                            capture="environment" 
                            onChange={(event) => {
                                this.setState({
                                    image: URL.createObjectURL(event.target.files[0])
                                })
                            }}
                    />
                    Camera
                </label>
            </div>
        </body>
    );
}

export default Camera;
