import './Camera.css';

function Camera() {
    handleImageChange = (event) => {
        this.setState({
          image: URL.createObjectURL(event.target.files[0])
        })
    }

    return(
        <body>
            <div class='camera'>
                <label>
                    <button type="button">Camera</button>
                    <input style={{display: 'none'}} type="file" accept="image/*" capture="environment" onChange={this.handleImageChange}></input>
                </label>
            </div>
        </body>
    );
}

export default Camera;
