import './Sidebar.css';
import {Camera} from './Camera';

function Sidebar() {
    return(
        <body>
            <div id="popup" class="sidebar">
                <div id = "controls_row" class = "vertical_div">
                        <a href="javascript:void(0)" class="closebtn" onClick={() => {
                            document.getElementById("popup").style.height = "0";
                        }}>Ã—</a>
                    <button id="cameraButton" className="cameraButton" onClick={() => {Camera.startCamera()}}>Camera</button>
                </div>
                <div id = "location_info_row" className = "vertical_div">

                </div>
                <div id = "legacy_photo_row" className = "vertical_div">

                </div>
                <div id = "user_photo_row" className = "vertical_div">

                </div>
                <div id="cameraPreview" className="cameraPreview">
                    <button id="stopCamera" className="stopCamera" onClick={() => {Camera.stopCamera()}}></button>
                    <button id="captureImage" className="captureImage" onClick={() => {Camera.captureImage()}}></button>
                    <button id="flipCamera" className="flipCamera" onClick={() => {Camera.flipCamera()}}></button>
                </div>
            </div>
        </body>
    );
}

export default Sidebar;
