function Camera(){
    const screenshot = document.getElementById('screenshotButton');
    const cameraOptions = document.getElementById('select');
    const video = document.getElementById('video');
    const canvas = document.getElementById('canvas');
    const screenshotImage = document.getElementById('display-style');

    const constraints = {
        video: {
            width: {
            min: 1280,
            ideal: 1920,
            max: 2560,
            },
            height: {
            min: 720,
            ideal: 1080,
            max: 1440
            },
        }
    };

    cameraOptions.onchange = () => {
        const updatedConstraints = {
            ...constraints,
            deviceId: {
            exact: cameraOptions.value
            }
        };

        startStream(updatedConstraints, video, screenshot);
    };

    if ('mediaDevices' in navigator && navigator.mediaDevices.getUserMedia) {
        const updatedConstraints = {
        ...constraints,
        deviceId: {
            exact: cameraOptions.value
        }
        };
        startStream(updatedConstraints, video, screenshot);
    }

    const doScreenshot = () => {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        canvas.getContext('2d').drawImage(video, 0, 0);

        var image = document.createElement('img');
        image.id = "screenshotImage";
        image.className = "screenshot-image d-none";
        image.src = canvas.toDataURL('image/jpeg', 1.0);

        screenshotImage.classList.remove('d-none');
        screenshotImage.appendChild(image);

        var okButton = document.createElement('button');

        var retakeButton = document.createElement('button');
    };

    screenshot.onclick = doScreenshot;

    const getCameraSelection = async () => {
        const devices = await navigator.mediaDevices.enumerateDevices();
        const videoDevices = devices.filter(device => device.kind === 'videoinput');
        const options = videoDevices.map(videoDevice => {
            return `<option value="${videoDevice.deviceId}">${videoDevice.label}</option>`;
        });
        cameraOptions.innerHTML = options.join('');
    };

    getCameraSelection();
}

async function startStream(constraints, video, screenshot) {
    const stream = await navigator.mediaDevices.getUserMedia(constraints);
    handleStream(stream, video, screenshot);
}

function handleStream(stream, video, screenshot) {
    video.srcObject = stream;
    screenshot.classList.remove('d-none');
}

export default Camera;
