function Camera() {
    return(
        <body>
            <div class='camera'>
                <label>
                    <input  id="camera"
                            style={{display: 'none'}}
                            type="file" 
                            accept="image/*"
                            capture="camera"
                            onchange="readURL(this);"
                    />
                    Camera
                </label>
            </div>
        </body>
    );
}

