import { startCamera } from './Camera';


var [latitude, longitude] = [0, 0];

function setPinLatLong(lat, long) {
    latitude = lat;
    longitude = long;
}

function getPinLatLong() {
    return [latitude, longitude]
}

function locationChecker(userLat, userLong) {
    const coords = getPinLatLong();
    const pinLat = coords[0];
    const pinLong = coords[1];

    if(Math.abs(userLat - pinLat) > 1 ) {
        alert("You must be closer to location to take a picture.");
    } else {
        startCamera();
    }
}



function getDistanceFromLatLonInKm(lat1, lon1, lat2, lon2) {
    var R = 6371; // Radius of the earth in km
    var dLat = deg2rad(lat2-lat1);  // deg2rad below
    var dLon = deg2rad(lon2-lon1); 
    var a = 
        Math.sin(dLat/2) * Math.sin(dLat/2) +
        Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * 
        Math.sin(dLon/2) * Math.sin(dLon/2)
        ; 
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
    var d = R * c; // Distance in km
    return d;
}

function deg2rad(deg) {
    return deg * (Math.PI/180)
}
