function Camera() {
    return(
        <div className='camera'>
            <label>
                <input  id="camera"
                        style={{display: 'none'}}
                        type="file" 
                        accept="image/*"
                        capture="camera"
                        onChange={readURL(this)}
                />
                Camera
            </label>
        </div>
    );
}

