import './CameraScreen.css';
import camera from './Camera';

function cameraScreen() {
    return(
        '<video id="video" autoPlay></video>' +
        '<canvas id="canvas" className="d-none"></canvas>' +

        '<div className="video-options">' +
           '<select name="" id="select" className="custom-select">' +
                '<option value="">Select camera</option>' +
            '</select>' +
        '</div>' +

        '<img id="screenshot" className="screenshot-image d-none" alt="" />' +

        '<div id="controls" className="controls">' + 
            '<button className="btn btn-danger play" title="Play"><i data-feather="play-circle"></i></button>' +
            '<button className="btn btn-info pause d-none" title="Pause"><i data-feather="pause"></i></button>' +
            '<button className="btn btn-outline-success screenshot d-none" title="ScreenShot"><i data-feather="image"></i></button>' +
        '</div>' +
        '<a href="javascript:void(0)" id="close-button" className="close-button" onClick={() => {handleClose()}>×</a>'
    );
}

async function handleScreen(screen){
    const constraints = {
        video: {
            width: {
            min: 1280,
            ideal: 1920,
            max: 2560,
            },
            height: {
            min: 720,
            ideal: 1080,
            max: 1440
            },
        }
    };

    await navigator.mediaDevices.getUserMedia(constraints);
    screen.innerHTML = cameraScreen();
    screen.style.display = "block";
    screen.webkitRequestFullscreen();

    camera();

    console.log(screen);

}

function handleClose(){

}

export default handleScreen;
