import './Camera.css';

function Camera() {
    return(
        <body>
            <div class='camera'>
                <label>
                    <input type="file" accept="image/*" capture="environment" onChange={(event) => {this.setState({
                                                                                                image: URL.createObjectURL(event.target.files[0])
                                                                                            })
                                                                                        }}/>
                </label>
            </div>
        </body>
    );
}

export default Camera;
