import './Camera.css';
import { CameraPreview } from '@capacitor-community/camera-preview';

class Camera {

    constructor() {
        this.image = null;
        camera = document.getElementById("cameraPreview");
        [closeButton, captureButton, flipButton] = camera.querySelectorAll('button');
    }

    startCamera() {
        console.log("Clicked");
        const cameraPreviewOptions = {
            position: 'rear',
            parent: 'cameraPreview',
            class: 'cameraPreview',
        };
        CameraPreview.start(cameraPreviewOptions);
    }

    async stopCamera() {
        await CameraPreview.stop();
    }

    async captureImage() {
        const cameraPreviewOptions = {
            quality: 90,
        };

        const result = await CameraPreview.capture(cameraPreviewOptions);
        this.image = `data:image/jpeg;base64,${result.value}`;
        this.stopCamera();
    }

    flipCamera() {
        CameraPreview.flip();
    }
}

export const { startCamera, stopCamera, captureImage, flipCamera } = new Camera();
