import './Camera.css';

function Camera() {
    return(
        <body>
            <div class='camera'>
                <label>
                    <button type="button">Camera</button>
                    <input  style={{display: 'none'}}
                            type="file"
                            accept="image/*"
                            capture="environment"
                            onChange={
                                (event) => {
                                    this.setState({
                                        image: URL.createObjectURL(event.target.files[0])
                                    })
                                }
                            }>
                    </input>
                </label>
            </div>
        </body>
    );
}

export default Camera;
