import './CameraScreen.css';
import camera from './Camera';

function cameraScreen() {
    return(
        '<span id="display-style" className="display-style">' +
            '<video id="video" autoPlay></video>' +
            '<canvas hidden id="canvas" className="canvas"></canvas>' +

            '<div id="video-options" className="video-options">' +
                '<select name="" id="select" className="custom-select">' +
                    '<option value="">Select camera</option>' +
                '</select>' +
            '</div>' +

            '<div id="screenshotControls" className="screenshotControls">' +
                '<button id="retakePhoto">Retake</button>' +
                '<button id="usePhoto">Use Photo</button>' +
            '</div>' +

            '<div id="controls" className="controls">' +
                '<button id="screenshotButton" className="screenshotButton"><img src="https://www.pikpng.com/pngl/m/348-3481477_iphone-camera-icon-transparent-photograph-clipart.png" /></button>' +
            '</div>' +
            '<a href="javascript:void(0)" id="close-button" className="close-button">×</a>' +
        '</span>'
    );
}

function handleScreen(screen){
    const constraints = {
        video: {
            width: {
                min: 1280,
                ideal: 1920,
                max: 2560,
            },
            height: {
                min: 720,
                ideal: 1080,
                max: 1440
            },
        }
    };
    //const stream = await navigator.mediaDevices.getUserMedia(constraints);
    if (navigator.mediaDevices){
        navigator.mediaDevices.getUserMedia(constraints)
        screen.innerHTML = cameraScreen();
        screen.style.display = "block";
        //makeFullscreen(screen);

        const screenshotControls = document.getElementById('screenshotControls');
        screenshotControls.style.display = "none";

        camera();

        const closeButton = document.getElementById("close-button");
        closeButton.onclick = () => {
            handleClose();
        }
    }
}

function handleClose(){
    const screen = document.getElementById("display-cover");
    screen.style.display = "none";
}

function makeFullscreen(screen) {
    if (screen.requestFullscreen) {
        screen.requestFullscreen();
    } else if (screen.mozRequestFullScreen) { // Firefox
        screen.mozRequestFullScreen();
    } else if (screen.webkitRequestFullscreen) { // Chrome, Safari, and Opera
        screen.webkitRequestFullscreen();
    } else if (screen.msRequestFullscreen) { // IE/Edge
        screen.msRequestFullscreen();
    }
}

export default handleScreen;
