import React, { useRef, useEffect } from 'react';
import { onValue } from "firebase/database";
import mapboxgl from 'mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css' //Must have this otherwise pins don't anchor
import firebase from './query/connect_to_firebase.js';
import { getHistoricalImage, retrieveCapturedImage } from './Sidebar';
import { setPinLatLong, setUserLatLong } from './Location';
import { setIndex } from './CameraScreen';

mapboxgl.accessToken = 'pk.eyJ1Ijoibmlja2ZhZGRpcyIsImEiOiJja3Vwemt0amwwbnVoMnZwZ2dreW81OTQwIn0.qerYpAFHJUlYynqbympvAA';

const MapContainer = () => {
    const mapContainer = useRef(null);

    useEffect(() => {
        const map = new mapboxgl.Map({
            container: mapContainer.current,
            style: 'mapbox://styles/nickfaddis/ckurogok00w1l18mmby6l9zsf',
            center: [-90.221932, 38.632511],
            zoom: 15
        });

        map.addControl(new mapboxgl.NavigationControl(), 'top-right');

        let geolocate = new mapboxgl.GeolocateControl({
            positionOptions: {
                enableHighAccuracy: true
            },
            trackUserLocation: true,
            showUserHeading: true
        });
        map.addControl(geolocate);

        geolocate.on('geolocate', function (position) {
            const userLat = position.coords.latitude;
            const userLong = position.coords.longitude;
            console.log('lat, lng', userLat, userLong);

            setUserLatLong(userLat, userLong);
        });

        onValue(firebase, (snapshot) => {
            const data = snapshot.val();
            const keys = Object.keys(data).map(function(key){ return {key, ...data[key]} });

            keys.forEach(element => {
                const marker = new mapboxgl.Marker();
                var long = element.lng;
                var lat = element.lat;
                marker.setLngLat([long,lat]);
                marker.addTo(map);

                marker.getElement().addEventListener('click', () => {
                    setIndex(element.key);
                    retrieveCapturedImage(element.key);
                    setPinLatLong(lat, long);

                    //Variables for sidebar divs
                    var location_info_div = document.getElementById('location_info_row');
                    var legacy_photo_div = document.getElementById('legacy_photo_row');
                    var user_photo_div = document.getElementById('user_photo_row');

                    //Make popup visible by modifying height
                    document.getElementById("popup").style.height = "50%";

                    //Clear each div of past elements
                    while(location_info_div.childElementCount > 0) {
                        location_info_div.removeChild(location_info_div.lastChild);
                    }
                    while(legacy_photo_div.childElementCount > 0) {
                        //legacy_photo_div.lastChild.removeEventListener('click');
                        legacy_photo_div.removeChild(legacy_photo_div.lastChild);
                    }
                    while(user_photo_div.childElementCount > 0) {
                        //user_photo_div.lastChild.removeEventListener('click');
                        user_photo_div.removeChild(user_photo_div.lastChild);
                    }

                    //Initialize the name and date elements
                    var name_par = document.createElement('h1');
                    var name = document.createTextNode(element.Name);
                    name_par.id = 'location_name'
                    name_par.appendChild(name);
                    location_info_div.appendChild(name_par);
                    if (element.date !== "NA") {
                        var date_par = document.createElement('p');
                        var date = document.createTextNode(element.date);
                        date_par.id = 'location_date'
                        date_par.appendChild(date);
                        location_info_div.appendChild(date_par);
                    }

                    var image = document.createElement('img');
                    image.id = "thumb_image";
                    image.src = element.image_url;
                    console.log(element.image_url);
                    legacy_photo_div.appendChild(image);
                    getHistoricalImage(element.image_url);

                    /* 
                        Event listener for allowing users to make image thumbnail full screen by tapping thumbnail or larger image. Creates
                        initial full screen image and flips visibility value when tapping either the thumbnail image or the full screen image.


                        TODO: Fix event listener bug. Currently, every time an image is created it creates another set of event listeners
                        So it only works every other time as it will show->hide->show due to multiple listeners getting triggered
                    */
                    document.addEventListener('click', function(e){
                        var target = e.target;
                        //Only
                        if ((target.id === "thumb_image" || target.id === "large_image") && element.image_url !== "NA") {
                            if (document.getElementById("large_image")) {
                                //The image already exists, so simply flip its visiblity around
                                var full_image = document.getElementById('large_image');
                                full_image.id = "large_image";

                                if (full_image.style.visibility === 'hidden') {
                                    console.log("showing")
                                    full_image.style.visibility = 'visible';
                                    full_image.src = target.src;
                                } else {
                                    console.log("hiding")
                                    full_image.style.visibility = 'hidden';
                                }
                            } else {
                                //The image doesn't exist, so we have to make it
                                var full_image = document.createElement('img');
                                full_image.id = "large_image";
                                full_image.src = target.src;
                                full_image.class = "large_image";
                                //Styling won't apply from the CSS file so doing it manually for now
                                full_image.style.position = 'absolute';
                                full_image.style.objectFit = 'contain';
                                full_image.style.backgroundColor = 'black';
                                full_image.style.zIndex = '999'
                                full_image.style.top='0';
                                full_image.style.left='0';
                                full_image.style.width='100vw';
                                full_image.style.height='100vh'
                                //Add to 'App' so it appears on top
                                document.getElementById("App").appendChild(full_image)
                            }
                        }
                    }, false)
                })
            })
        });

        //TODO: Create radio button to change between labeled and non-labeled styles
        //map.setStyle('mapbox://styles/mapbox/outdoors-v11');
    }, []);

    return <div className="map-container" style={{height: '100vh'}} ref={ mapContainer } />;
}

export default MapContainer;
