import './Camera.css';

function Camera() {
    return(
        <body>
            <div class='camera'>
                <label for="camera">Camera</label>
                    <input  id='camera'
                            //style={{display: 'none'}}
                            type="file" 
                            accept="image/*" 
                            capture="environment" 
                            onChange={(event) => {
                                this.setState({
                                    image: URL.createObjectURL(event.target.files[0])
                                })
                            }}
                    />


            </div>
        </body>
    );
}

export default Camera;
